<template>
  <div>
    <BasicTable @register="registerTable">
      <template #toolbar>
        <Button type="primary" @click="handleAdd">添加</Button>
        <!-- <Button type="primary"> 回收站 </Button> -->
      </template>
      <template #icon="{ record }">
        <Image
          fallback="data:image/png;base64,"
          :width="50"
          :src="record.icon"
        />
      </template>
      <template #action="{ record }">
        <TableAction
          :actions="[
            {
              icon: 'clarity:note-edit-line',
              tooltip: '编辑',
              onClick: handleEdit.bind(null, record),
            },
            {
              icon: 'wpf:delete',
              color: 'error',
              tooltip: '删除',
              popConfirm: {
                title: '是否确认删除',
                confirm: handleDelete.bind(null, record),
              },
            },
          ]"
        />
      </template>
    </BasicTable>
    <PreviewModal @register="registerModal" @success="handleSuccess" />
  </div>
</template>

<script setup lang="ts">
  import { Button, Image } from 'ant-design-vue';
  import { BasicTable, useTable, TableAction } from '/@/components/Table';
  import { modelListApi, modelRemoveApi } from '/@/api/codegen/module';
  import PreviewModal from './addModuleModal.vue';
  import { columns } from './moduleData';
  import { useModal } from '/@/components/Modal';

  const [registerModal, { openModal }] = useModal();
  const [registerTable, { reload }] = useTable({
    title: '模块列表',
    api: modelListApi,
    columns,
    formConfig: {
      labelWidth: 80,
    },
    showTableSetting: true,
    bordered: true,
    clickToRowSelect: false,
    showIndexColumn: true,
    pagination: {
      current: 1,
      pageSize: 10,
    },
    actionColumn: {
      width: 240,
      title: '操作',
      dataIndex: 'action',
      slots: { customRender: 'action' },
      fixed: undefined,
    },
  });

  const handleEdit = (record: Recordable) => {
    openModal(true, {
      isUpdate: true,
      record,
    });
  };
  const handleDelete = async (record: Recordable) => {
    try {
      await modelRemoveApi(record.id);
      reload();
    } catch (error) {
      console.log(error);
    }
  };
  const handleAdd = () => {
    openModal(true, {
      isUpdate: false,
    });
  };
  const handleSuccess = () => {
    reload();
  };
</script>

<style scoped></style>
