<template>
  <div>
    <BasicTable @register="registerTable" @selection-change="onSelectionChange">
      <template #imgUrl="{ record }">
        <Image
          fallback="data:image/png;base64,"
          :width="50"
          :src="record.imgUrl"
        />
      </template>
      <template #toolbar>
        <a-button type="primary" @click="handleCreate"> 新增 </a-button>
        <importFile @success="importExcel" title="导入" />
        <a-button type="primary" @click="exportExcel"> 导出 </a-button>
      </template>
      <template #action="{ record }">
        <TableAction
          :actions="[
            {
              icon: 'ic:baseline-edit-note',
              tooltip: '编辑轮播图',
              onClick: handleEditModal.bind(null, record),
            },
            {
              icon: 'wpf:delete',
              color: 'error',
              tooltip: '删除轮播图',
              popConfirm: {
                title: '是否确认删除',
                confirm: handleDelete.bind(null, record),
              },
            },
          ]"
        />
      </template>
    </BasicTable>
    <SysBannerModal @register="registerModal" @success="handleSuccess" />
  </div>
</template>
<script lang="ts" setup>
  import { message, Image } from 'ant-design-vue';
  import { importFile } from '/@/components/System';
  import { BasicTable, useTable, TableAction } from '/@/components/Table';
  import { pageApi, removeApi, exportApi, importApi } from '/@/api/sys/sysBanner';
  import SysBannerModal from './SysBannerModal.vue';
  import { columns, searchFormSchema } from './sysBanner.data';
  import { reactive } from 'vue';
  import { useModal } from '/@/components/Modal';
  const [registerModal, { openModal }] = useModal();
  const [registerTable, { reload }] = useTable({
    title: '轮播图管理列表',
    api: pageApi,
    columns,
    formConfig: {
      labelWidth: 80,
      schemas: searchFormSchema,
      autoSubmitOnEnter: true,
    },
    clickToRowSelect: false,
    rowSelection: {
      type: 'checkbox',
    },
    useSearchForm: true,
    showTableSetting: true,
    bordered: true,
    showIndexColumn: true,
    pagination: {
      current: 1,
      pageSize: 10,
    },
    actionColumn: {
      width: 120,
      title: '操作',
      dataIndex: 'action',
      slots: { customRender: 'action' },
    },
  });
  let selectId = reactive<any[]>([]);
  // 创建轮播图管理
  const handleCreate = () => {
    openModal(true, {
      isUpdate: false,
    });
  };
  // 编辑轮播图管理 Modal
  const handleEditModal = (record: Recordable) => {
    openModal(true, {
      record,
      isUpdate: true,
    });
  };
  // 删除轮播图管理
  const handleDelete = async (record: Recordable) => {
    await removeApi(record.id);
    message.success('删除轮播图管理成功');
    reload();
  };
  // 成功
  function handleSuccess() {
    reload();
  }

  // excel导入
  const importExcel = async (e) => {
    try {
      let params = {
        file: e,
      };
      const res = await importApi(params);
      let data = JSON.parse(JSON.stringify(res));
      if (data.data.code == 1) {
        message.success(data.data.msg);
        return;
      }
      message.success('导入成功');
      reload();
    } catch (error) {
      console.log(error);
    }
  };

  // 导出
  const exportExcel = async () => {
    try {
      let params = selectId.toString();
      const res = await exportApi(params);
      const blob = new Blob([res.data], { type: 'application/vnd.ms-excel' });
      const url = window.URL.createObjectURL(blob);
      window.open(url, '_blank');
      message.success('导出成功');
    } catch (error) {
      console.log(error);
    }
  };
  const onSelectionChange = async (e) => {
    selectId = reactive<any[]>([]);
    e.rows.forEach((it) => {
      selectId.push(it.id);
    });
  };
  // 批量删除
</script>
